import { Component, OnInit } from '@angular/core';

@Component({
  selector: 'app-header',
  templateUrl: './header.component.html',
  styleUrls: ['./header.component.scss']
})
export class HeaderComponent implements OnInit{

  lockScroll = () => {
    if(window.innerWidth < 500) {
      document.body.classList.toggle('lock-scroll')
    }
  }

  showNav = () => {
    console.log("it works")
  }

  ngOnInit(): void {
  }
}
<nav class="navbar">
    <div class="header-title">
        <h1>FitFolio</h1>
    </div>
    <div class="toggle-button" (click)="lockScroll()" (click)="showNav()">
        <span class="bar"></span>
        <span class="bar"></span>
        <span class="bar"></span>
    </div>
    <div class="navbar-links">
        <ul>
        <li><a (click)="lockScroll()" href="#">My Workouts</a></li>
        <li><a (click)="lockScroll()" href="#">Login/Register</a></li>
        </ul>

    </div>
</nav>
