import { Component, OnInit } from '@angular/core';
import { AppServiceService } from '../app-service.service';
import { HttpClient, HttpHeaders } from '@angular/common/http';

@Component({
  selector: 'app-home-page',
  templateUrl: './home-page.component.html',
  styleUrls: ['./home-page.component.scss']
})
export class HomePageComponent implements OnInit{
  constructor(private http: HttpClient) {
}

workouts:any=[];

  ngOnInit(){
    this.http.get('https://exercisedb.p.rapidapi.com/exercises?limit=1', {
      headers: new HttpHeaders({
        'X-RapidAPI-Key': '1f65bfcc53mshe628dd3ca1bfac4p1763d3jsn41b07dec96eb',
		    'X-RapidAPI-Host': 'exercisedb.p.rapidapi.com'
      })
    }).subscribe(result => {
      this.workouts = result
      console.log(result)
    })
  }


}
<div *ngFor="let work of workouts" class="workouts">
    <div class="workout">
        <!-- <img src="{{work.gifUrl}}"> -->
        <h1 class="workout-name"> {{work.name}}</h1>
        <p class="workout-target"> {{work.target}}</p>
    </div>
</div>
